mapper.insert:
start transaction;
    set @next_id = null;
    select max(id) + 1 into @next_id from discussion where article_id = #{article_id};
    insert into discussion(article_id, id, publisher_id, content, publish_time, origin_id, parent_id)
        values(#{article_id}, ifnull(@next_id, 1), #{publisher_id}, #{content}, now(), #{origin_id}, #{parent_id});
commit;
